update.foreach:
UPDATE alarm_send_status ala

	SET ala.send_fail_count = ( ala.send_fail_count + 1 )


	ala.alarm_statistics_id = #{bean.alarmStatisticsId}

mapper.select:
select s.id, s.longi,s.lati, s.phName, s.phId, s.ticketId, s.deviceName, s.deviceId, s.alarmInfo, s.alarmReason, s.alarDescription, s.endDate, s.startDate, s.alarmTime, s.alarmLevel from ( SELECT concat(ALARM.device_code,'_',ALARM.function_name,'_',date_format(ALARM.start_date,'%Y-%m-%d %H:%i:%s')) as id, longi,lati, DL.name phName, DL.id phId, alarm.ticketId ticketId, D.name deviceName, concat(D.id,'') deviceId, ATD.FUNCTION_REMARKS alarmInfo, ATD.alarm_reason alarmReason, ATD.task_description alarDescription, date_format(ALARM.END_DATE,'%Y-%m-%d %H:%i:%s') endDate, date_format(ALARM.START_DATE,'%Y-%m-%d %H:%i:%s') startDate, truncate(TIMESTAMPDIFF(SECOND,Start_DATE,End_Date)/3600,2) alarmTime, case when alarm_level = '1' then '一级' when alarm_level = '2' then '二级' when alarm_level = '3' then '三级' when alarm_level = '4' then '四级' else '' end alarmLevel FROM alarm_statistics alarm JOIN alarm_task_description atd ON atd.function_name = alarm.function_name JOIN device d ON d.id = alarm.device_code JOIN pump_house dl ON dl.id = d.id_pump_house WHERE d.id IN ( SELECT authobject_id FROM permission_info ) and (start_date >= substring(now(),1,10) or end_date >= substring(now(),1,10)) and alarm_status = '1' and confirm_status = '0'
and dl.name like CONCAT('%',#{pumpHouseName},'%')
and dl.ID_REGION = #{areaCode}
) as s where NOT EXISTS ( SELECT alarm_statistics_id FROM alarm_send_status ala WHERE ala.alarm_statistics_id = s.id and ( ala.is_success = 'Y' or ala.send_fail_count >= 3 ) and ala.valid_flag = 'Y'
)

SELECT concat(ALARM.device_code,'__',ALARM.function_name,'__',date_format(ALARM.start_date,'%Y-%m-%d %H:%i:%s')) as id, longi,lati, DL.name phName, alarmSet.alarmSetValue, DL.id phId, atd.function_name functionName, DL.ADDRESS address, alarm.ticketId ticketId, D.name deviceName, atd.alarm_type_remarks as alarmTypeRemarks, concat(D.id,'') deviceId, ATD.FUNCTION_REMARKS alarmInfo, ATD.alarm_reason alarmReason, ATD.task_description alarDescription, date_format(ALARM.END_DATE,'%Y-%m-%d %H:%i:%s') endDate, date_format(ALARM.START_DATE,'%Y-%m-%d %H:%i:%s') startDate, truncate(TIMESTAMPDIFF(SECOND,Start_DATE,End_Date)/3600,2) alarmTime, case when alarm_level = '1' then '一级' when alarm_level = '2' then '二级' when alarm_level = '3' then '三级' when alarm_level = '4' then '四级' else '' end alarmLevel FROM alarm_statistics alarm JOIN alarm_task_description atd ON atd.function_name = alarm.function_name JOIN device d ON d.id = alarm.device_code JOIN pump_house dl ON dl.id = d.id_pump_house left JOIN getAlarmSetValue alarmSet ON alarmSet.idDevice=alarm.device_code and atd.function_name =alarmSet.functionName WHERE d.id IN ( SELECT authobject_id FROM permission_info WHERE role_id IN (SELECT role_id FROM sys_user_role WHERE user_id = #{userId}) or role_id in (select role_id as authobject_id from sys_office_role_new where office_id in (select office_id from sys_user where id = #{userId})) GROUP BY authobject_id ) and (start_date > substring(now(),1,10) or end_date > substring(now(),1,10)) and alarm_status = '1' and confirm_status = '0'
and dl.id in
#{item}
and dl.ID_REGION = #{areaCode}
and dl.name like CONCAT('%',#{pumpHouseName},'%')
and ATD.FUNCTION_REMARKS like CONCAT('%',#{alarmInfo},'%')
and alarm_level= #{alarmLevel}
group by id
order by ${sortName} ${sortOrder}
order by start_date desc
limit 20

SELECT DL.name phName, D.name deviceName, D.id deviceId, DL.address, ATD.FUNCTION_REMARKS alarmInfo, ATD.task_description alarDescription, atd.alarm_type_remarks as alarmTypeRemarks, date_format(ALARM.END_DATE,'%Y-%m-%d %H:%i:%s') endDate, date_format(ALARM.START_DATE,'%Y-%m-%d %H:%i:%s') startDate, truncate(TIMESTAMPDIFF(SECOND,Start_DATE,End_Date)/3600,2) alarmTime, alarm_reason as alarmReason, case when alarm_level = '1' then '一级' when alarm_level = '2' then '二级' when alarm_level = '3' then '三级' when alarm_level = '4' then '四级' else '' end alarmLevel, case when alarm.confirm_status = '1' then '已确认' when alarm.confirm_status = '2' then '忽略' else '无' end confirmStat, alarm.org_user_name userName, alarm.confirm_reason confirmReson, date_format(confirm_date,'%Y-%m-%d %H:%i:%s') confirmDate, alarm.ticketId ticketId FROM alarm_statistics alarm JOIN alarm_task_description atd ON atd.function_name = alarm.function_name JOIN device d ON d.id = alarm.device_code JOIN pump_house dl ON dl.id = d.id_pump_house WHERE d.id IN ( SELECT authobject_id FROM permission_info WHERE role_id IN (SELECT role_id FROM sys_user_role WHERE user_id = #{userId}) or role_id in (select role_id as authobject_id from sys_office_role_new where office_id in (select office_id from sys_user where id = #{userId})) GROUP BY authobject_id ) AND ALARM.END_DATE BETWEEN #{beginTime} AND #{endTime}
AND ( DL.name LIKE concat('%', #{keyword} ,'%') OR ATD.FUNCTION_REMARKS LIKE concat('%', #{keyword} ,'%') or D.id LIKE concat('%', #{keyword} ,'%') )
and atd.alarm_level = #{type}
and dl.name like CONCAT('%',#{pumpHouseName},'%')
and dl.ID_REGION = #{areaCode}
and D.name like CONCAT('%',#{deviceName},'%')
and ATD.FUNCTION_REMARKS like CONCAT('%',#{alarmInfo},'%')
and truncate(TIMESTAMPDIFF(SECOND,ALARM.Start_DATE,ALARM.End_Date)/3600,2) >= #{startAlarmTime}
and truncate(TIMESTAMPDIFF(SECOND,ALARM.Start_DATE,ALARM.End_Date)/3600,2) <= #{endAlarmTime}
order by ${sortName} ${sortOrder}
ORDER BY ALARM.END_DATE desc
limit #{pageNumber},#{pageSize}

SELECT DL.name phName, D.name deviceName, D.id deviceId, ATD.FUNCTION_REMARKS alarmInfo, ATD.task_description alarDescription, atd.alarm_type_remarks as alarmTypeRemarks, date_format(ALARM.END_DATE,'%Y-%m-%d %H:%i:%s') endDate, date_format(ALARM.START_DATE,'%Y-%m-%d %H:%i:%s') startDate, truncate(TIMESTAMPDIFF(SECOND,Start_DATE,End_Date)/3600,2) alarmTime, case when alarm_level = '1' then '一级' when alarm_level = '2' then '二级' when alarm_level = '3' then '三级' when alarm_level = '4' then '四级' else '' end alarmLevel, case when alarm.confirm_status = '1' then '已确认' when alarm.confirm_status = '2' then '忽略' else '无' end confirmStat, alarm.org_user_name userName, alarm.confirm_reason confirmReson FROM alarm_statistics alarm JOIN alarm_task_description atd ON atd.function_name = alarm.function_name JOIN device d ON d.id = alarm.device_code JOIN pump_house dl ON dl.id = d.id_pump_house WHERE d.id IN ( SELECT authobject_id FROM permission_info WHERE role_id IN (SELECT role_id FROM sys_user_role WHERE user_id = #{userId}) or role_id in (select role_id as authobject_id from sys_office_role_new where office_id in (select office_id from sys_user where id = #{userId})) GROUP BY authobject_id ) AND ALARM.END_DATE BETWEEN #{beginTime} AND #{endTime}
AND ( DL.name LIKE concat('%', #{keyword} ,'%') OR ATD.FUNCTION_REMARKS LIKE concat('%', #{keyword} ,'%') or D.id LIKE concat('%', #{keyword} ,'%') )
and atd.alarm_level = #{type}
and dl.name like CONCAT('%',#{pumpHouseName},'%')
and dl.ID_REGION = #{areaCode}
ORDER BY ALARM.END_DATE desc

SELECT count(*) FROM alarm_statistics alarm JOIN alarm_task_description atd ON atd.function_name = alarm.function_name JOIN device d ON d.id = alarm.device_code JOIN pump_house dl ON dl.id = d.id_pump_house WHERE D.id IN ( SELECT authobject_id FROM permission_info WHERE role_id IN (SELECT role_id FROM sys_user_role WHERE user_id = #{userId}) or role_id in (select role_id as authobject_id from sys_office_role_new where office_id in (select office_id from sys_user where id = #{userId})) GROUP BY authobject_id ) AND ALARM.END_DATE BETWEEN #{beginTime} AND #{endTime}
AND ( DL.name LIKE concat('%', #{keyword} ,'%') OR ATD.FUNCTION_REMARKS LIKE concat('%', #{keyword} ,'%') or D.id LIKE concat('%', #{keyword} ,'%') )
and atd.alarm_level = #{type}
and dl.name like CONCAT('%',#{pumpHouseName},'%')
and dl.ID_REGION = #{areaCode}
and D.name like CONCAT('%',#{deviceName},'%')
and ATD.FUNCTION_REMARKS like CONCAT('%',#{alarmInfo},'%')
and truncate(TIMESTAMPDIFF(SECOND,ALARM.Start_DATE,ALARM.End_Date)/3600,2) >= #{startAlarmTime}
and truncate(TIMESTAMPDIFF(SECOND,ALARM.Start_DATE,ALARM.End_Date)/3600,2) <= #{endAlarmTime}

SELECT case when alarm_level = '1' then '一级' when alarm_level = '2' then '二级' when alarm_level = '3' then '三级' when alarm_level = '4' then '四级' else '' end deviceName, count(alarm_level) deviceId FROM alarm_statistics alarm JOIN alarm_task_description atd ON atd.function_name = alarm.function_name JOIN device d ON d.id = alarm.device_code JOIN pump_house dl ON dl.id = d.id_pump_house WHERE D.id IN ( SELECT authobject_id FROM permission_info WHERE role_id IN (SELECT role_id FROM sys_user_role WHERE user_id = #{userId}) or role_id in (select role_id as authobject_id from sys_office_role_new where office_id in (select office_id from sys_user where id = #{userId})) GROUP BY authobject_id ) AND ALARM.END_DATE BETWEEN #{beginTime} AND #{endTime}
AND ( DL.name LIKE concat('%', #{keyword} ,'%') OR ATD.FUNCTION_REMARKS LIKE concat('%', #{keyword} ,'%') or D.id LIKE concat('%', #{keyword} ,'%') )
and atd.alarm_level = #{type}
and dl.name like CONCAT('%',#{pumpHouseName},'%')
and dl.ID_REGION = #{areaCode}
and D.name like CONCAT('%',#{deviceName},'%')
and ATD.FUNCTION_REMARKS like CONCAT('%',#{alarmInfo},'%')
and truncate(TIMESTAMPDIFF(SECOND,ALARM.Start_DATE,ALARM.End_Date)/3600,2) >= #{startAlarmTime}
and truncate(TIMESTAMPDIFF(SECOND,ALARM.Start_DATE,ALARM.End_Date)/3600,2) <= #{endAlarmTime}
group by alarm_level

