SELECT subString(end_date,1,10) deviceName, count(end_date) deviceId FROM alarm_statistics alarm JOIN alarm_task_description atd ON atd.function_name = alarm.function_name JOIN device d ON d.id = alarm.device_code JOIN pump_house dl ON dl.id = d.id_pump_house WHERE D.id IN ( SELECT authobject_id FROM permission_info WHERE role_id IN (SELECT role_id FROM sys_user_role WHERE user_id = #{userId}) or role_id in (select role_id as authobject_id from sys_office_role_new where office_id in (select office_id from sys_user where id = #{userId})) GROUP BY authobject_id ) AND ALARM.END_DATE BETWEEN #{beginTime} AND #{endTime}
AND ( DL.name LIKE concat('%', #{keyword} ,'%') OR ATD.FUNCTION_REMARKS LIKE concat('%', #{keyword} ,'%') or D.id LIKE concat('%', #{keyword} ,'%') )
and atd.alarm_level = #{type}
and dl.name like CONCAT('%',#{pumpHouseName},'%')
and dl.ID_REGION = #{areaCode}
and D.name like CONCAT('%',#{deviceName},'%')
and ATD.FUNCTION_REMARKS like CONCAT('%',#{alarmInfo},'%')
and truncate(TIMESTAMPDIFF(SECOND,ALARM.Start_DATE,ALARM.End_Date)/3600,2) >= #{startAlarmTime}
and truncate(TIMESTAMPDIFF(SECOND,ALARM.Start_DATE,ALARM.End_Date)/3600,2) <= #{endAlarmTime}
group by subString(end_date,1,10)

mapper.update:
update alarm_statistics set confirm_status = #{alarmType},
org_user_name = #{userName},confirm_reason = #{confirmReson},confirm_url = #{configUrl},
confirm_date = now()
where device_code = #{deviceId} and function_name = #{deviceName}
    and start_date = #{startDate}

mapper.select:
SELECT CONCAT(a.device_code, '_', a.function_name, '_', DATE_FORMAT(a.start_date, '%Y-%m-%d %H:%i:%s')) AS id, p.name AS phName, case when ds.label is null then '低 区' else ds.label end deviceName, at.alarm_type alarmType, DATE_FORMAT(a.start_date, '%Y-%m-%d %H:%i:%s') alarmTime, at.task_description alarDescription FROM alarm_statistics a JOIN device d ON d.id = a.device_code JOIN pump_house p ON p.id = d.id_pump_house JOIN alarm_task_description at ON at.function_name = a.function_name left JOIN (SELECT * FROM sys_dict WHERE type = 'device_location') ds ON ds.value = d.LOCATION where a.device_code = #{deviceId} and a.function_name = #{deviceName} and a.start_date = #{startDate}
and p.name like CONCAT('%',#{pumpHouseName},'%')
and p.ID_REGION = #{areaCode}
limit 1
