SELECT deviceData.ID_SERVICE as serviceId, fu.MEMO AS serviceName, deviceData.DATE_TIME as serviceDate, deviceData.PV as serviceValue, fu.UNIT AS serviceUnit FROM ( SELECT * FROM ${dbName}
AND ID_SERVICE IN
#{item}
AND ID_DEVICE = #{deviceId}
AND ID_DEVICE in
#{item}
AND DATE_TIME > date_sub(now(), interval 3 Minute)
ORDER BY DATE_TIME DESC LIMIT 200 ) deviceData INNER JOIN services service ON service.ID = deviceData.ID_SERVICE INNER JOIN function fu on fu.id = service.ID_FUNCTION GROUP BY deviceData.id_service

SELECT se.id AS serviceId, fu.MEMO AS serviceName, se.CODE AS serviceCode, se.ID_DEVICE AS deviceId, se.ID_FUNCTION AS functionId, fu.DATA_TYPE AS type, fu.UNIT AS unit, fu.IS_CUMULATIVE, fu.IS_PARAM_SETTING, fu.IS_ALARM, fu.IS_PARAM_STATUS, IFNULL(se.RangeMaximum, fu.MAX_RV) as max, IFNULL(se.RangeMinimum, fu.MIN_RV) as min, IFNULL(se.RATIO, fu.RATIO) as multiple, se.Default_Visible AS visible FROM services se INNER JOIN function fu on fu.id = se.ID_FUNCTION WHERE
ID_DEVICE = #{deviceId}
ID_DEVICE in
#{item}
order by fu.id

SELECT deviceData.ID_DEVICE as deviceId, deviceData.ID_SERVICE as serviceId, fu.TYPE AS type, fu.MEMO AS serviceName, deviceData.DATE_TIME as serviceDate, IFNULL(case truncate(deviceData.pv * case when service.ratio is null then 1 else service.ratio end ,2) when '0.00' then '0' else truncate(deviceData.pv * case when service.ratio is null then 1 else service.ratio end ,2) end, '-') as serviceValue, fu.UNIT AS serviceUnit FROM ( SELECT * FROM ${dbName}
AND ID_DEVICE = #{deviceId}
AND ID_DEVICE in
#{item}
AND `ID_SERVICE` = #{serviceId}
AND ID_SERVICE in
#{item}
AND DATE_TIME > date_sub(now(), interval 15 Second)
ORDER BY DATE_TIME DESC LIMIT 100000 ) deviceData INNER JOIN services service ON service.ID = deviceData.ID_SERVICE INNER JOIN function fu on fu.id = service.ID_FUNCTION where service.node_display='0' GROUP BY service.data_sort
