SELECT
temp1.id_device idDevice,temp1.id_service idService, temp1.FUNCTION_MEMO tagName, s.code,temp1.pv
FROM
(select * from service_values_current where
id_device in

    #{item}

 ) temp1
JOIN
services s ON s.id = temp1.id_service where s.code in

    #{item}

SELECT temp.id_device,temp.id_service, s.name tag_name, s.unit, (temp.pv * case when ratio is null then 1 else ratio end) as pv, s.code FROM (SELECT temp1.id_device,temp1.id_service, temp1.tag_name, temp1.pv FROM (SELECT * FROM ${dateNumber} WHERE date_time > DATE_SUB(NOW(), INTERVAL 15 Second) AND id_device IN
#{item.deviceId}
ORDER BY date_time DESC) temp1 GROUP BY id_service) temp JOIN services s ON s.id = temp.id_service where s.code in
#{item}
ORDER BY s.data_sort ASC

SELECT SUBSTRING_INDEX( a.id_service, '\\', - 1 ) pointCode, IFNULL( CASE TRUNCATE ( pv * CASE WHEN ratio IS NULL THEN 1 ELSE ratio END, 2 ) WHEN '0.00' THEN '0' ELSE TRUNCATE ( pv * CASE WHEN ratio IS NULL THEN 1 ELSE ratio END, 2 ) END, '-' ) pointValue FROM ( SELECT * FROM ( SELECT * FROM ${tableName} WHERE ID_DEVICE = #{idDevice} AND date_time >= date_sub( now( ), INTERVAL 3 MINUTE )
and ID_SERVICE in
#{idService}
ORDER BY DATE_TIME DESC LIMIT 0,10000) a GROUP BY a.id_service ) a LEFT JOIN services s ON s.id = a.id_service;

SELECT CODE FROM services WHERE ID_DEVICE in
#{item.deviceId}
AND CODE LIKE ('%FrequencyOutput')

select
  *
from sys_dict

    type = #{type}
    and del_flag = '0';
