mapper.update:
update ticket_to_do a
set a.user_id= #{userId},a.status=#{status}
where a.ticket_Id= #{ticketId}

update alarm_statistics a
set a.ticketId= #{ticketId}
where a.device_code=#{deviceCode}
and a.start_date=#{eventTime}

update ticket a
set a.`status`= #{status}
where a.ticket_id=#{ticketId}

mapper.select:
SELECT t.ticket_id ticketId, t.ticket_type ticketType, ( CASE WHEN t.ticket_type = '1' THEN '告警工单' WHEN t.ticket_type = '2' THEN '巡检工单' WHEN t.ticket_type = '3' THEN '维保工单' ELSE '' END ) ticketTypeName, t.title title, t.comments comments, t.ticket_level ticketLevel, t.address address, t.event_time eventTime, t.device_code deviceCode, t.pump_id pumpId, t.area_id areaId, t.channel channel, t.report_name reportName, t.report_phone reportPhone, t.start_time startTime, t.end_time endTime, t.create_Date createDate, t.update_Date updateDate, t.create_by createByStr, t.update_by updateByStr, t.ticket_reason ticketReason, t.ticket_description ticketDescription, t.`status` AS STATUS, ( CASE
WHEN t.`status` = '1' THEN '新建' WHEN t.`status` = '2' THEN '待接单' WHEN t.`status` = '3' THEN '处理中' WHEN t.`status` = '4' THEN '审核中' WHEN t.`status` = '5' THEN '完成' ELSE '' END ) currentStatusName, t.biz_id bizId, de.`NAME` deviceName, p.`NAME` pumpName FROM ticket t LEFT JOIN device de ON de.ID = t.device_code LEFT JOIN pump_house p ON p.id = de.ID_PUMP_HOUSE
INNER JOIN ticket_to_do td ON td.ticket_id = t.ticket_id and td.user_Id = #{handleUserId}
WHERE t.valid_flag = '1'
and t.title like concat('%',#{title},'%')
and p.`NAME` like concat('%',#{pumpName},'%')
and t.ticket_type = #{ticketType}
and t.create_Date >= #{createBeginTime}
and t.create_Date <= #{createEndTime}
and t.start_time >= #{startBeginTime}
and t.start_time <= #{startEndTime}
order by t.create_Date desc

SELECT t.ticket_id ticketId, t.ticket_type ticketType, ( CASE WHEN t.ticket_type = '1' THEN '告警工单' WHEN t.ticket_type = '2' THEN '巡检工单' WHEN t.ticket_type = '3' THEN '维保工单' ELSE '' END ) ticketTypeName, t.title title, t.comments comments, t.ticket_level ticketLevel, t.address address, t.event_time eventTime, t.device_code deviceCode, t.pump_id pumpId, t.area_id areaId, t.channel channel, t.report_name reportName, t.report_phone reportPhone, t.start_time startTime, t.end_time endTime, t.create_Date createDate, t.update_Date updateDate, t.create_by createByStr, t.update_by updateByStr, t.ticket_reason ticketReason, t.ticket_description ticketDescription, t.`status` AS STATUS, ( CASE
WHEN t.`status` = '1' THEN '新建' WHEN t.`status` = '2' THEN '待接单' WHEN t.`status` = '3' THEN '处理中' WHEN t.`status` = '4' THEN '审核中' WHEN t.`status` = '5' THEN '完成' ELSE '' END ) currentStatusName, t.biz_id bizId, de.`NAME` deviceName, p.`NAME` pumpName, group_concat(u.`name`) allHandleUser FROM ticket t LEFT JOIN device de ON de.ID = t.device_code LEFT JOIN pump_house p ON p.id = de.ID_PUMP_HOUSE LEFT JOIN ticket_to_do td ON td.ticket_id = t.ticket_id LEFT JOIN sys_user u ON u.id=td.user_id WHERE t.valid_flag = '1'
and t.title like concat('%',#{title},'%')
and p.`NAME` like concat('%',#{pumpName},'%')
and t.ticket_type = #{ticketType}
and t.create_Date >= #{createBeginTime}
and t.create_Date <= #{createEndTime}
and t.start_time >= #{startBeginTime}
and t.start_time <= #{startEndTime}
group by t.ticket_id order by t.create_Date desc

SELECT t.ticket_id ticketId, t.ticket_type ticketType, ( CASE WHEN t.ticket_type = '1' THEN '告警工单' WHEN t.ticket_type = '2' THEN '巡检工单' WHEN t.ticket_type = '3' THEN '维保工单' ELSE '' END ) ticketTypeName, t.title title, t.comments comments, t.ticket_level ticketLevel, t.address address, t.event_time eventTime, t.device_code deviceCode, t.pump_id pumpId, t.area_id areaId, t.channel channel, t.report_name reportName, t.report_phone reportPhone, t.start_time startTime, t.end_time endTime, t.create_Date createDate, t.update_Date updateDate, t.create_by createByStr, t.update_by updateByStr, t.ticket_reason ticketReason, t.ticket_description ticketDescription, t.`status` AS STATUS, ( CASE
WHEN t.`status` = '1' THEN '新建' WHEN t.`status` = '2' THEN '待接单' WHEN t.`status` = '3' THEN '处理中' WHEN t.`status` = '4' THEN '审核中' WHEN t.`status` = '5' THEN '完成' ELSE '' END ) currentStatusName, t.biz_id bizId, de.`NAME` deviceName, p.`NAME` pumpName, g.approveOpinion, g.approveOperation, u.`name` ticketLogName FROM ticket t LEFT JOIN device de ON de.ID = t.device_code LEFT JOIN pump_house p ON p.id = de.ID_PUMP_HOUSE LEft JOIN ticket_log g ON t.ticket_id= g.ticket_id LEFT JOIN sys_user u ON u.id=g.create_by
WHERE t.valid_flag = '1'
and t.title like concat('%',#{title},'%')
and p.`NAME` like concat('%',#{pumpName},'%')
and t.ticket_type = #{ticketType}
and t.create_Date >= #{createBeginTime}
and t.create_Date <= #{createEndTime}
