mapper.select:
select count(id) from pump_house where id in ( select id_pump_house from device di where di.ID IN (SELECT AUTHOBJECT_ID FROM permission_info pi WHERE AUTH_TYPE = 'DEVICE' AND ROLE_ID IN (SELECT ROLE_ID FROM sys_user_role WHERE user_id = #{id}) OR ROLE_ID IN (SELECT role_id FROM sys_office_role_new WHERE office_id = (SELECT office_id FROM sys_user WHERE id = #{id}))) )
and id_project = #{projectId}
and ID_REGION = #{area}
and name like concat('%',#{pumpHouseName},'%')

select id as pumpHouseId, name as pumpHouseName, address as pumpHouseAddress, memo as memo from pump_house
1=1
and id=#{pumpHouseId}

select aa.id AS "id", aa.name AS "pumpHouseName", aa.address AS "address", aa.device_manufacturers as deviceManufacturers, aa.device_manufacturers_information as deviceManufacturersInformation, aa.self_control_manufacturers as selfControlManufacturers, aa.self_control_manufacturers_information as selfControlManufacturersInformation, aa.construction_side as constructionSide, aa.construction_side_information as constructionSideInformation, aa.Construction as construction, aa.Construction_information as constructionInformation, aa.property as property, aa.property_information as propertyInformation, aa.cell_name as cellName, aa.cell_adress as cellAdress, j.name as projectId, r.name as areaId from pump_house aa
left join regional r on r.id = aa.ID_REGION left join project j on j.id = aa.id_project
where aa.id in ( select id_pump_house from device di where di.ID IN (SELECT AUTHOBJECT_ID FROM permission_info pi WHERE AUTH_TYPE = 'DEVICE' AND ROLE_ID IN (SELECT ROLE_ID FROM sys_user_role WHERE user_id = #{id}) OR ROLE_ID IN (SELECT role_id FROM sys_office_role_new WHERE office_id = (SELECT office_id FROM sys_user WHERE id = #{id}))) )
and aa.id_project = #{projectId}
and aa.ID_REGION = #{area}
and aa.name like concat('%',#{pumpHouseName},'%')
and aa.construction_side like concat('%',#{constructionSide},'%')
limit #{minMun} , #{maxNum}

select al.name as alarmId,count(al.name) as remarks from ( select aa.id , r.name from pump_house aa join regional r on r.id = aa.ID_REGION join project j on j.id = aa.id_project join device d on d.id_pump_house = aa.id where d.ID IN (SELECT AUTHOBJECT_ID FROM permission_info pi WHERE AUTH_TYPE = 'DEVICE' AND ROLE_ID IN (SELECT ROLE_ID FROM sys_user_role WHERE user_id = #{id}) OR ROLE_ID IN (SELECT role_id FROM sys_office_role_new WHERE office_id = (SELECT office_id FROM sys_user WHERE id = #{id})))
and aa.id_project = #{projectId}
and aa.ID_REGION = #{areaId}
and aa. name like concat('%',#{pumpHouseName},'%')
GROUP BY aa.ID ) al group by al.name

select ds.label as alarmId, count(ds.label) as remarks from ( select d.LOCATION,dd.label from ( select case when LOCATION is null then '1' else LOCATION end LOCATION from device where id_pump_house in ( select id from pump_house where 1 =1
and id_project = #{projectId}
and ID_REGION = #{areaId}
and name like concat('%',#{pumpHouseName},'%')
) and ID IN (SELECT AUTHOBJECT_ID FROM permission_info pi WHERE AUTH_TYPE = 'DEVICE' AND ROLE_ID IN (SELECT ROLE_ID FROM sys_user_role WHERE user_id = #{id}) OR ROLE_ID IN (SELECT role_id FROM sys_office_role_new WHERE office_id = (SELECT office_id FROM sys_user WHERE id = #{id}))) ) d left join sys_dict dd on d.LOCATION = dd.value where dd.type = 'device_location' ) ds group by ds.label
