mapper.update:
update ticket_to_do a
set a.user_id= #{userId},a.status=#{status}
where a.ticket_Id= #{ticketId}

   update alarm_statistics a
set a.ticketId= #{ticketId}
where a.device_code=#{deviceCode}
and a.start_date=#{eventTime}

mapper.select:
SELECT t.ticket_id ticketId, t.ticket_type ticketType, ( CASE WHEN t.ticket_type = '1' THEN '告警工单' WHEN t.ticket_type = '2' THEN '巡检工单' WHEN t.ticket_type = '3' THEN '维保工单' ELSE '' END ) ticketTypeName, t.title title, t.comments comments, t.ticket_level ticketLevel, t.address address, t.event_time eventTime, t.device_code deviceCode, t.pump_id pumpId, t.area_id areaId, t.channel channel, t.report_name reportName, t.report_phone reportPhone, t.start_time startTime, t.end_time endTime, t.create_Date createDate, t.update_Date updateDate, t.create_by createByStr, t.update_by updateByStr, t.ticket_reason ticketReason, t.ticket_description ticketDescription, t.`status` AS STATUS, t.dept_id as deptId, ( CASE WHEN t.`status` = '0' THEN '暂存' WHEN t.`status` = '1' THEN '新建' WHEN t.`status` = '2' THEN '待接单' WHEN t.`status` = '3' THEN '处理中' WHEN t.`status` = '4' THEN '审核中' WHEN t.`status` = '5' THEN '完成' ELSE '' END ) currentStatusName, t.biz_id bizId, de.`NAME` deviceName, su.name createName, p.`NAME` pumpName FROM ticket t LEFT JOIN device de ON de.ID = t.device_code LEFT JOIN pump_house p ON p.id = de.ID_PUMP_HOUSE LEFT JOIN sys_user su on su.id=t.create_by
INNER JOIN ticket_to_do td ON td.ticket_id = t.ticket_id and td.user_Id = #{handleUserId}
WHERE t.valid_flag = '1'
and t.title like concat('%',#{title},'%')
and t.status =#{status}
LOCATE(#{item}, t.pump_id) > 0
and t.ticket_type = #{ticketType}
and t.ticket_id like concat('%',#{ticketId},'%')
and su.name like concat('%',#{createName},'%')
and t.create_Date >= #{createBeginTime}
and t.create_Date <= #{createEndTime}
and t.start_time >= #{startBeginTime}
and t.start_time <= #{startEndTime}
order by ${sortName} ${sortOrder}
order by t.create_Date desc

SELECT t.ticket_id ticketId, t.ticket_type ticketType, ( CASE WHEN t.ticket_type = '1' THEN '告警工单' WHEN t.ticket_type = '2' THEN '巡检工单' WHEN t.ticket_type = '3' THEN '维保工单' ELSE '' END ) ticketTypeName, t.title title, t.comments comments, t.ticket_level ticketLevel, t.address address, t.event_time eventTime, t.device_code deviceCode, t.pump_id pumpId, t.area_id areaId, t.channel channel, t.report_name reportName, t.report_phone reportPhone, t.start_time startTime, t.end_time endTime, t.create_Date createDate, t.update_Date updateDate, t.create_by createByStr, su.name createName, t.update_by updateByStr, t.ticket_reason ticketReason, t.ticket_description ticketDescription, t.`status` AS STATUS, t.dept_id as deptId, ( CASE WHEN t.`status` = '0' THEN '暂存' WHEN t.`status` = '1' THEN '新建' WHEN t.`status` = '2' THEN '待接单' WHEN t.`status` = '3' THEN '处理中' WHEN t.`status` = '4' THEN '审核中' WHEN t.`status` = '5' THEN '完成' ELSE '' END ) currentStatusName, t.biz_id bizId, de.`NAME` deviceName, p.`NAME` pumpName FROM ticket t LEFT JOIN device de ON de.ID = t.device_code LEFT JOIN pump_house p ON p.id = de.ID_PUMP_HOUSE
LEFT JOIN sys_user su on su.id=t.create_by INNER JOIN ticket_log log ON t.ticket_id=log.TICKET_ID and log.CREATE_BY = #{handleUserId} WHERE t.valid_flag = '1'
and t.title like concat('%',#{title},'%')
and t.status =#{status}
and u.`name` like concat('%',#{allHandleUser},'%')
and su.name like concat('%',#{createName},'%')
and de.`NAME` like concat('%',#{deviceName},'%')
LOCATE(#{item}, t.pump_id) > 0
and t.ticket_id like concat('%',#{ticketId},'%')
and t.ticket_type = #{ticketType}
and t.create_Date >= #{createBeginTime}
and t.create_Date <= #{createEndTime}
and t.start_time >= #{startBeginTime}
and t.start_time <= #{startEndTime}
group by t.ticket_id
order by ${sortName} ${sortOrder}
order by t.create_Date desc

SELECT t.ticket_id ticketId, t.ticket_type ticketType, ( CASE WHEN t.ticket_type = '1' THEN '告警工单' WHEN t.ticket_type = '2' THEN '巡检工单' WHEN t.ticket_type = '3' THEN '维保工单' ELSE '' END ) ticketTypeName, t.title title, t.comments comments, t.ticket_level ticketLevel, t.address address, t.event_time eventTime, t.device_code deviceCode, t.pump_id pumpId, t.area_id areaId, t.channel channel, ( CASE WHEN t.channel = '1' THEN '系统报警' WHEN t.channel = '2' THEN '二供平台' WHEN t.channel = '3' THEN '定期计划' WHEN t.channel = '4' THEN '物业保修' ELSE '' END ) channelName, t.report_name reportName, t.report_phone reportPhone, t.start_time startTime, t.end_time endTime, t.create_Date createDate, t.update_Date updateDate, t.create_by createByStr, t.update_by updateByStr, t.ticket_reason ticketReason, t.ticket_description ticketDescription, t.`status` AS STATUS, t.dept_id as deptId, ( CASE WHEN t.`status` = '0' THEN '暂存' WHEN t.`status` = '1' THEN '新建' WHEN t.`status` = '2' THEN '待接单' WHEN t.`status` = '3' THEN '处理中' WHEN t.`status` = '4' THEN '审核中' WHEN t.`status` = '5' THEN '完成' ELSE '' END ) currentStatusName, t.biz_id bizId, de.`NAME` deviceName1, p.`NAME` pumpName FROM ticket t LEFT JOIN device de ON de.ID = t.device_code LEFT JOIN pump_house p ON p.id = de.ID_PUMP_HOUSE LEFT JOIN sys_user su on su.id=t.create_by WHERE t.valid_flag = '1'
and t.ticket_id in
#{item}
and t.title like concat('%',#{title},'%')
and t.status =#{status}
and t.address like concat('%',#{address},'%')
and p.`NAME` like concat('%',#{pumpName},'%')
and t.ticket_type = #{ticketType}
and t.ticket_id like concat('%',#{ticketId},'%')
and su.name like concat('%',#{createName},'%')
and t.create_Date >= #{createBeginTime}
and t.create_Date <= #{createEndTime}
and t.start_time >= #{startBeginTime}
and t.start_time <= #{startEndTime}

SELECT t.ticket_id ticketId, t.ticket_type ticketType, t.ticket_Distribute_By ticketDistributeBy, ( CASE WHEN t.ticket_type = '1' THEN '告警工单' WHEN t.ticket_type = '2' THEN '巡检工单' WHEN t.ticket_type = '3' THEN '维保工单' ELSE '' END ) ticketTypeName, t.title title, t.comments comments, t.ticket_level ticketLevel, t.address address, t.event_time eventTime, t.device_code deviceCode, t.pump_id pumpId, t.area_id areaId, t.channel channel, ( CASE WHEN t.channel = '1' THEN '系统报警' WHEN t.channel = '2' THEN '二供平台' WHEN t.channel = '3' THEN '定期计划' WHEN t.channel = '4' THEN '物业保修' ELSE '' END ) channelName, t.report_name reportName, t.report_phone reportPhone, t.start_time startTime, t.end_time endTime, t.create_Date createDate, t.update_Date updateDate, t.create_by createByStr, t.update_by updateByStr, t.ticket_reason ticketReason, t.ticket_description ticketDescription, t.`status` AS status, ( CASE WHEN t.`status` = '0' THEN '暂存' WHEN t.`status` = '1' THEN '新建' WHEN t.`status` = '2' THEN '待分发' WHEN t.`status` = '3' THEN '处理中' WHEN t.`status` = '4' THEN '审核中' WHEN t.`status` = '5' THEN '完成' ELSE '' END ) currentStatusName, t.biz_id bizId, de.`NAME` deviceName, p.`NAME` pumpName, t.dept_id deptId FROM ticket t LEFT JOIN device de ON de.ID = t.device_code LEFT JOIN pump_house p ON p.id = de.ID_PUMP_HOUSE
WHERE t.valid_flag = '1'
and t.ticket_Id=#{ticketId}
